mapper.select:
SELECT * FROM rule WHERE deleted IS NULL

    AND taxon_key = #{taxonKey}


    AND dataset_key = #{datasetKey}


    AND project_id = #{projectId}


    AND id IN (
      SELECT DISTINCT rule_id FROM comment WHERE deleted IS NULL AND comment LIKE '%' || #{comment} || '%'
    )

ORDER BY created DESC

SELECT created_by AS username, count(*) AS ruleCount, count(DISTINCT dataset_key) as contextCount, SUM(ARRAY_LENGTH(supported_by,1)) AS supportCount, SUM(ARRAY_LENGTH(contested_by,1)) AS contestCount FROM rule WHERE deleted IS NULL
AND taxon_key = #{taxonKey}
AND dataset_key = #{datasetKey}
AND project_id = #{projectId}
GROUP BY created_by ORDER BY ruleCount DESC
